    },
    {
      "id": "f3pwuJ0NqV",
      "type": "code",
      "data": {
        "code": "const barGeometry = new THREE.BoxGeometry(0.5, 1, 0.5);\nconst barMaterial = new THREE.MeshStandardMaterial({ color: 0x00ff00 });\nconst bars = [];\n\nfor (let i = 0; i < 10; i++) {\n  const bar = new THREE.Mesh(barGeometry, barMaterial);\n  bar.position.set(i - 5, 0.5, 0);\n  bars.push(bar);\n  scene.add(bar);\n}",
        "language": "javascript",
        "showlinenumbers": true
      }
