    {
      "id": "_vZigmd77l",
      "type": "code",
      "data": {
        "code": "function updateBars(data) {\n  bars.forEach((bar, index) => {\n    bar.scale.y = data[index];\n    bar.position.y = data[index] / 2;\n  });\n}",
        "language": "javascript",
